public:
    int pathSum(TreeNode *root, int sum)
    {
        int result = 0;
        std::vector<int> path;
        this->traversalDFS(root, sum, result, path);
        return result;
    }

    void traversalDFS(TreeNode *root, int sum, int &result, std::vector<int> &path)
    {
        if (root == nullptr)
        {
            return;
        }
        int prePathSize = int(path.size());
        path.push_back(root->val); // 记录中间节点，组成全路径

        // 统计改路径中满足条件的路径数（倒着尝试，包含最后一个节点。不包含最后加入节点的情况，曾经递归过程已统计过）
        int curSum = 0;
        for (int i = path.size() - 1; i >= 0; --i)
        {
            curSum += path[i];
            if (curSum == sum)
            {
                ++result;
            }
        }

        traversalDFS(root->left, sum, result, path);
        traversalDFS(root->right, sum, result, path);
        path.pop_back();
    }
